import { useState } from 'react';
import css from '../../../css/style.scss';
import pic from '../../../images/external_pic/pa-bulgogi.jpg';


// 나중에 이미지 가져와서 저장후 img : 변수에 넣으삼 
//사진 없어서 스시만 넣음 ㅅㄱ
import pic1 from '../../../images/external_pic/sushi.jpg';


const allStores = [
  { title: '김밥천국', sort: '분식', price: '3500원', category: '분식', img: pic1 },
  { title: '한식당', sort: '한식', price: '8000원', category: '한식', img: pic1 },
  { title: '홍콩반점', sort: '중식', price: '9000원', category: '중식', img: pic1 },
  { title: '오사카스시', sort: '일식', price: '12000원', category: '일식', img: pic1 },
  { title: '미국스테이크', sort: '양식', price: '20000원', category: '양식', img: pic1 },
  { title: 'BHC치킨', sort: '치킨', price: '18000원', category: '치킨', img: pic1 },
  { title: '고기굽는집', sort: '고기', price: '15000원', category: '고기', img: pic1 },
  { title: '감자탕집', sort: '탕', price: '10000원', category: '탕', img: pic1 },
  { title: '카페라떼', sort: '카페', price: '4500원', category: '카페', img: pic1 },
  { title: '스윗디저트', sort: '디저트', price: '6000원', category: '디저트', img: pic1 },
];

const categories = ['전체', '한식', '중식', '일식', '양식', '치킨', '고기', '탕', '분식', '카페', '디저트'];







function List() {
  const [selectedCategory, setSelectedCategory] = useState('전체');

  const filteredStores =
    selectedCategory === '전체'
      ? allStores
      : allStores.filter((store) => store.category === selectedCategory);


    let [like_count,setLike_count] = useState(0)

    
  return (
    <div className="list-wrap">
      <h1>오늘의 맛집 추천</h1>

      {/* 카테고리 버튼임 */}
      <div className="category-btns" style={{ marginBottom: '20px' }}>
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}      
          >
            {cat}
          </button>
        ))}
      </div>

      {/* 가게 리스트임 */}
      <ul>
        {filteredStores.map((store, index) => (
          <li key={index}>
            <a href="#">
              <div>
                <img src={store.img} alt={store.title} />
              </div>
              <div className="info">
                <p className="title">{store.title} <span onClick={()=>{
                    let copy = like_count;
                    like_count += 1
                    setLike_count(like_count)
                }}>1</span></p>
                <p className="sort">{store.sort} 전문점 / ♥ {like_count}</p>
                <p className="price">가격 {store.price}</p>
              </div>
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default List;
