import { useState } from 'react';
import css from '../../../css/style.scss';
import pic from '../../../images/external_pic/pa-bulgogi.jpg';

const allStores = [
  { title: '가게1', sort: '카페', price: '2500원', category: '카페' },
  { title: '가게2', sort: '디저트', price: '3000원', category: '디저트' },
  { title: '가게3', sort: '카페', price: '2000원', category: '카페' },
  { title: '가게4', sort: '디저트', price: '3500원', category: '디저트' },
  { title: '가게5', sort: '카페', price: '4000원', category: '카페' },
  { title: '가게6', sort: '디저트', price: '1500원', category: '디저트' },
];

const categories = ['전체', '카페', '디저트'];

function List() {
  const [selectedCategory, setSelectedCategory] = useState('전체');

  const filteredStores =
    selectedCategory === '전체'
      ? allStores
      : allStores.filter((store) => store.category === selectedCategory);

  return (
    <div className="list-wrap">
      

      {/* 카테고리 버튼 */}
      <div className="category-btns">
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            style={{
              marginRight: '10px',
              backgroundColor: selectedCategory === cat ? '#ddd' : '#f1f1f1',
            }}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* 가게 리스트 */}
      <ul>
        {filteredStores.map((store, index) => (
          <li key={index}>
            <a href="#">
              <div>
                <img src={pic} alt={store.title} />
              </div>
              <div className="info">
                <p className="title">{store.title}</p>
                <p className="sort">{store.sort} 전문점</p>
                <p className="price">가격 {store.price}</p>
              </div>
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default List;
